var width = window.innerWidth,
    height = window.innerHeight,
    DESELECTED_RADIUS = 10,
    SELECTED_RADIUS = 20,
    fill = d3.scale.category20();
/*var width = 960,
	height = 500;*/

// mouse event vars
var selectedNodeObj = null;
var selectedNodeDOMObject = null;

// init svg
var outer = d3.select("#chart")
  .append("svg")
    .attr("width", width)
    .attr("height", height)
    .attr("pointer-events", "all");

//defines what arrowhead looks like
outer.append('svg:defs').append('svg:marker')
    .attr('id', 'start-arrow')
    .attr('viewBox', '0 -5 10 10')
    .attr('refX', 4)
    .attr('markerWidth', 3)
    .attr('markerHeight', 3)
    .attr('orient', 'auto')
  .append('svg:path')
    .attr('d', 'M10,-5L0,0L10,5')
    .attr('fill', '#000');

//
var vis = outer
 	.append('g')
 	//special transform...
 	.attr("transform", "translate(" + width / 2 + "," + height / 2 + ")")
    .call(d3.behavior.zoom()
    .scaleExtent([.5,10])	//limits zooming
    .on("zoom", rescale))
    .on("dblclick.zoom", null)
  	.append('svg:g');

  	
//creates the background
vis.append('svg:rect')
	.attr("class","overlay")
	.attr("x",-width/2)
	.attr("y", -height/2)
    .attr('width', width)
    .attr('height', height)
    .attr('fill','white');

// init force layout
var force = d3.layout.force()
    .size([.75,.75])
    .nodes(initNodes)
    .links(initLinks)
    .linkDistance(3)
    .linkStrength(1)
    .charge(-1000)
    .gravity(1)
//    .size([width*=.5, height *= .5])
    .on("tick", tick);

// get layout properties
var nodes = force.nodes(),
    links = force.links();

//makes arrow heads
var link = vis.selectAll(".link").data(links)
    .enter().insert("svg:path")
    .attr("class", "link")
    .style('marker-start', 'url(#start-arrow)')

var node = vis.selectAll(".node").data(nodes)
    .enter().append("g")
    .attr("class", "node")
    .on("click", selectNode)
node.append("circle")
    .attr("r", DESELECTED_RADIUS)
node.append("text")
    .attr("dx", 12)
    .attr("dy", ".35em")
    .text(function(d) { return d.courseId; })

force.start();

function tick() {
  link.attr("x1", function(d) { return d.source.x; })
      .attr("y1", function(d) { return d.source.y; })
      .attr("x2", function(d) { return d.target.x; })
      .attr("y2", function(d) { return d.target.y; })
      .attr('d', function(d) {
        var deltaX = d.target.x - d.source.x,
        deltaY = d.target.y - d.source.y,
        dist = Math.sqrt(deltaX * deltaX + deltaY * deltaY),
        normX = deltaX / dist,
        normY = deltaY / dist,
        sourcePadding = DESELECTED_RADIUS,
        targetPadding = 0,
        sourceX = d.source.x + (sourcePadding * normX),
        sourceY = d.source.y + (sourcePadding * normY),
        targetX = d.target.x - (targetPadding * normX),
        targetY = d.target.y - (targetPadding * normY);
        return 'M' + sourceX + ',' + sourceY + 'L' + targetX + ',' + targetY;
      });
      
  node.attr("transform",
      function(d,i) {return "translate(" + d.x + ", " + d.y + ")"});
}

// pan and scale
function rescale() {
  trans=d3.event.translate;
  scale=d3.event.scale;

  // keep in screen
  trans[0] = Math.min(width / 2 * (scale - 1), Math.max(width / 2 * (1 - scale), trans[0]));
  trans[1] = Math.min(height / 2 * (scale - 1) + 230 * scale, Math.max(height / 2 * (1 - scale) - 230 * scale, trans[1]));

  vis.attr("transform",
      "translate(" + trans + ")"
      + " scale(" + scale + ")");
 // d3.layout.force().charge().translate(trans).scale(scale);
  d3.behavior.zoom().translate(trans);
}

function selectNode(d) {
  if (d == selectedNodeObj) {
    deselect();
  }
  else {
    if (selectedNodeObj)
      deselect();
    selectedNodeObj = d;
    selectedNodeDOMObject = this;
    d3.select(this).select("circle").transition()
      .duration(750)
      .attr("r", SELECTED_RADIUS);
    node.classed("node_selected", function(d) {
      return d === selectedNodeObj; });
  }
}

function deselect() {
  d3.select(selectedNodeDOMObject).select("circle").transition()
    .duration(750)
    .attr("r", DESELECTED_RADIUS);
  node.classed("node_selected", function(d) {
    return d === selectedNodeObj; });
  selectedNodeObj = null;
  selectedNodeDOMObject = null;
  node.classed("node_selected", false);
}
